

var app = angular.module('angapp',['ngAnimate','ngRoute','oitozero.ngSweetAlert']);

app.config( function($routeProvider) {
	$routeProvider
	.when('/',{
		templateUrl: 'templates/tmp.login.html'
	})
	.when('/login',{
		templateUrl: 'templates/tmp.login.html',
		controller:'loginController'
	})
	.when('/activate',{
		templateUrl: 'templates/tmp.activate.html',
		controller:'activateController'
	})
	.when('/signup', {
		templateUrl:'templates/tmp.signup.html',
		controller:'signupController'
	})
	.when('/admin', {
		templateUrl:'templates/tmp.dashboard.html',
		controller:'dashboardController'
	})
	.otherwise({
		redirectTo:'/'
	})
})